package com.xworkz.external;


import com.xworkz.internal.God;

public class Shiva implements God {

        @Override
        public void create() {
            System.out.println("Shiva creates the universe");
        }

        @Override
        public void protect() {
            System.out.println("Shiva protects the devotees");
        }

        @Override
        public void destroy() {
            System.out.println("Shiva destroys evil and negativity");
        }

        @Override
        public void bless() {
            System.out.println("Shiva blesses with peace and wisdom");
        }

        @Override
        public void punish() {
            System.out.println("Shiva punishes the wrongdoers");
        }

        @Override
        public void forgive() {
            System.out.println("Shiva forgives the truly repentant");
        }

        @Override
        public void listenPrayer() {
            System.out.println("Shiva listens to prayers");
        }

        @Override
        public void giveWisdom() {
            System.out.println("Shiva grants divine wisdom");
        }

        @Override
        public void showMiracle() {
            System.out.println("Shiva shows miraculous powers");
        }
    }

